filename = "/home/nicholas/Dropbox/Programming/Advent_of_Code/day1_input.txt"
with open(filename, 'r') as f:
    lines = f.readlines()
nums = [int(line) for line in lines]

# Part 1
summa = 0
for n in nums:
    summa += n
print(summa)


# Part 2
seen = set([0])
flag = True
summa = 0
while flag:
    for n in nums:
        summa += n
        if summa in seen:
            print('First repeat is {}'.format(summa))
            flag = False
            break
        else:
            seen.add(summa)