filename = "/home/nicholas/Dropbox/Programming/Advent_of_Code/day2_input.txt"
with open(filename, 'r') as f:
    lines = f.readlines()
twosum, threesum = (0, 0)
for line in lines:
    seen = {}
    for char in line:
        if char in seen:
            seen[char] += 1
        else:
            seen[char] = 1
    if 2 in seen.values():
        twosum+=1
    if 3 in seen.values():
        threesum+=1
checksum = twosum*threesum
print(checksum)